```javascript
async function delete__VAR:entity_singular__(id) {
  try {
    const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('auth_token')}`,
        'Accept': 'application/json'
      }
    });

    // Success cases
    if (response.status === 204 || response.status === 200 || response.status === 404) {
        console.log('✅ __VAR:entity_singular__ deleted');
        return true;
    }

    // Permission denied
    if (response.status === 403) {
        console.error('❌ Permission denied');
        alert('You do not have permission to delete this __VAR:entity_singular__');
        return false;
    }

    // Has dependencies
    if (response.status === 409) {
        const data = await response.json();
        console.error('❌ Cannot delete:', data.message);
        alert(data.message);
        return false;
    }

    throw new Error(`HTTP ${response.status}`);

  } catch (error) {
    console.error('Error deleting __VAR:entity_singular__:', error);
    alert('Failed to delete __VAR:entity_singular__');
    return false;
  }
}

// Usage
