```javascript
import { useState } from 'react';

function __VAR:entity_singular__DeleteButton({ id, name, onDeleted }) {
  const [deleting, setDeleting] = useState(false);

  const handleDelete = async () => {
    // Confirmation dialog
    const confirmed = window.confirm(
        `Are you sure you want to delete "__JSON:name__"?\n\n` +
        `This action cannot be undone.`
    );

    if (!confirmed) {
        return;
    }

    setDeleting(true);

    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        }
      });

      if (response.ok || response.status === 204 || response.status === 404) {
        // Success!
        if (onDeleted) {
          onDeleted(id);
        }
        showToast('__VAR:entity_singular__ deleted successfully', 'success');
      } else if (response.status === 403) {
        alert('You do not have permission to delete this __VAR:entity_singular__');
      } else if (response.status === 409) {
        const data = await response.json();
        alert(data.message || 'Cannot delete __VAR:entity_singular__ with related items');
      } else {
        throw new Error('Failed to delete');
      }
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setDeleting(false);
    }
  };

  return (
    <button
      onClick={handleDelete}
      disabled={deleting}
      className="btn-danger">
      {deleting ? 'Deleting...' : 'Delete'}
    </button>
  );
}
