        `"__JSON:name__" will be deleted in 5 seconds`,
        () => {
            // Undo clicked
            clearTimeout(undoTimeout);
            showToast('Deletion cancelled', 'info');
        }
    );

    // Set timeout for actual deletion
    const timeout = setTimeout(async () => {
        try {
            const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
                method: 'DELETE',
                headers: {
                    'Authorization': `Bearer __JSON:token__`
                }
            });

            if (response.ok || response.status === 204) {
                toast.close();
                showToast('__VAR:entity_singular__ deleted', 'success');
                onDeleted(id);
            }
        } catch (error) {
            console.error(error);
            showToast('Failed to delete', 'error');
        }
    }, 5000);

    setUndoTimeout(timeout);
  };

  return { deleteWithUndo };
}
