```javascript
async function bulkDelete__VAR:entity_plural__(ids) {
  const total = ids.length;
  let deleted = 0;
  let failed = 0;

  // Show progress
  showProgress(`Deleting __JSON:total__ items...`, 0);

  for (const id of ids) {
    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer __JSON:token__`
        }
      });

      if (response.ok || response.status === 204 || response.status === 404) {
        deleted++;
      } else {
        failed++;
      }
    } catch (error) {
      failed++;
    }

    // Update progress
    const progress = ((deleted + failed) / total) * 100;
    showProgress(`Deleted __JSON:deleted__ of __JSON:total__...`, progress);
  }

  // Show summary
  hideProgress();
  alert(`Deleted: __JSON:deleted__\nFailed: __JSON:failed__`);

  // Refresh list
  reloadList();
}

// Usage
const selectedIds = [1, 2, 3, 4, 5];
await bulkDelete__VAR:entity_plural__(selectedIds);
