<?php

namespace App\Http\Controllers;

use App\Models\__VAR:entity_singular__;
use Illuminate\Http\JsonResponse;
use Illuminate\Http\Response;

class __VAR:entity_singular__Controller extends Controller
{
    /**
     * Delete __VAR:entity_singular__
     */
    public function destroy(int $id): Response|JsonResponse
    {
        // Find resource
        $__VAR:entity_singular__ = __VAR:entity_singular__::find($id);

        // Check if exists
        if (!$__VAR:entity_singular__) {
            return response()->json([
                'message' => '__VAR:entity_singular__ not found.'
            ], 404);
        }

        // Authorization check
        $this->authorize('delete', $__VAR:entity_singular__);

        // Check for related items
        if ($__VAR:entity_singular__->hasRelatedItems()) {
            return response()->json([
                'message' => 'Cannot delete __VAR:entity_singular__ with related items.',
                'related_count' => $__VAR:entity_singular__->relatedItemsCount()
            ], 409);
        }

        // Soft delete or hard delete
        try {
            if (request()->query('force') === 'true') {
                // Force delete (permanent)
                $__VAR:entity_singular__->forceDelete();
            } else {
                // Soft delete (if enabled)
                $__VAR:entity_singular__->delete();
            }

            // Log deletion
            Log::info('__VAR:entity_singular__ deleted', [
                'id' => $id,
                'user_id' => auth()->id()
            ]);

            // Return 204 No Content (recommended)
            return response()->noContent();

            // Or return 200 with message
            // return response()->json([
            //     'message' => '__VAR:entity_singular__ deleted successfully'
            // ]);

        } catch (\Exception $e) {
            Log::error('Failed to delete __VAR:entity_singular__', [
                'id' => $id,
                'error' => $e->getMessage()
            ]);

            return response()->json([
                'message' => 'Failed to delete __VAR:entity_singular__'
            ], 500);
        }
    }

    /**
     * Restore soft deleted __VAR:entity_singular__
     */
    public function restore(int $id): JsonResponse
    {
        $__VAR:entity_singular__ = __VAR:entity_singular__::withTrashed()->find($id);

        if (!$__VAR:entity_singular__) {
            return response()->json([
                'message' => '__VAR:entity_singular__ not found.'
            ], 404);
        }

        if (!$__VAR:entity_singular__->trashed()) {
            return response()->json([
                'message' => '__VAR:entity_singular__ is not deleted.'
            ], 422);
        }

        $__VAR:entity_singular__->restore();

        return response()->json([
            'data' => $__VAR:entity_singular__,
            'message' => '__VAR:entity_singular__ restored successfully'
        ]);
    }
}
