import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function __VAR:entity_singular__Detail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [__VAR:entity_singular__, set__VAR:entity_singular__] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadData();
  }, [id]);

  const loadData = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('auth_token')}`
        },
        body: JSON.stringify({
          relations: ['user', 'category']
        })
      });

      if (response.ok) {
        const result = await response.json();
        set__VAR:entity_singular__(result.data);
      } else if (response.status === 404) {
        setError('__VAR:entity_singular__ not found');
        setTimeout(() => navigate('/__VAR:entity_plural__'), 2000);
      } else {
        setError('Failed to load __VAR:entity_singular__');
      }
    } catch (err) {
      setError('Network error');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={() => navigate('/__VAR:entity_plural__')}>
          Back to List
        </button>
      </div>
    );
  }

  if (!__VAR:entity_singular__) {
    return null;
  }

  return (
    <div className="detail-page">
      <h1>__VAR:entity_singular__ Details</h1>

      <div className="info-card">
        <div className="field">
          <label>ID:</label>
          <span>{item.id}</span>
        </div>

        {/* Display other fields */}

        <div className="field">
          <label>Created:</label>
          <span>{new Date(__VAR:entity_singular__.created_at).toLocaleDateString()}</span>
        </div>

        <div className="field">
          <label>Updated:</label>
          <span>{new Date(__VAR:entity_singular__.updated_at).toLocaleDateString()}</span>
        </div>
      </div>

      {/* Display relations */}
      { if has_relations }
      {item.user && (
        <div className="relation-card">
          <h2>User</h2>
          <p>{item.user.name}</p>
        </div>
      )}


      <div className="actions">
        <button onClick={() => navigate(`/__VAR:entity_plural__/__JSON:id__/edit`)}>
          Edit
        </button>
        <button onClick={() => navigate('/__VAR:entity_plural__')}>
          Back to List
        </button>
      </div>
    </div>
  );
}
