```javascript
import { ref, onMounted } from 'vue';
import { useRoute, useRouter } from 'vue-router';

export function use__VAR:entity_singular__Detail() {
  const route = useRoute();
  const router = useRouter();
  const __VAR:entity_singular__ = ref(null);
  const loading = ref(false);
  const error = ref(null);

  const load = async (id) => {
    loading.value = true;
    error.value = null;

    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          relations: ['user', 'category']
        })
      });

      if (response.ok) {
        const result = await response.json();
        __VAR:entity_singular__.value = result.data;
      } else if (response.status === 404) {
        error.value = '__VAR:entity_singular__ not found';
        setTimeout(() => {
          router.push('/__VAR:entity_plural__');
        }, 2000);
      } else {
        error.value = 'Failed to load';
      }
    } catch (err) {
      error.value = 'Network error';
    } finally {
      loading.value = false;
    }
  };

  onMounted(() => {
    if (route.params.id) {
      load(route.params.id);
    }
  });

  return {
    __VAR:entity_singular__,
    loading,
    error,
    load,
    reload: () => load(route.params.id)
  };
}

// In component
<script setup>
const { __VAR:entity_singular__, loading, error } = use__VAR:entity_singular__Detail();
</script>

<template>
  <div v-if="loading">Loading...</div>
  <div v-else-if="error">__VAR:error__</div>
  <div v-else-if="__VAR:entity_singular__">
    <h1>{{ __VAR:entity_singular__.name }}</h1>
    <!-- Display fields -->
  </div>
</template>
```


