     */
    public function show(Request $request, int $id): JsonResponse
    {
        // Find resource
        $__VAR:entity_singular__ = __VAR:entity_singular__::find($id);

        // Check if exists
        if (!$__VAR:entity_singular__) {
            return response()->json([
                'message' => '__VAR:entity_singular__ not found.'
            ], 404);
        }

        // Load relations if requested
        $relations = $request->input('relations', []);

        if (!empty($relations)) {
            if (in_array('all', $relations)) {
                // Load all defined relations
                $__VAR:entity_singular__->load(['user', 'category', 'tags']);
            } else {
                // Load specific relations
                $__VAR:entity_singular__->load($relations);
            }
        }

        // Return response
        return response()->json([
            'data' => $__VAR:entity_singular__
        ]);
    }
}
