```javascript
import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function Edit__VAR:entity_singular__Form() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({});
  const [originalData, setOriginalData] = useState({});
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  // Load existing data
  useEffect(() => {
    loadData();
  }, [id]);

  const loadData = async () => {
    setLoading(true);

    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        headers: {
          'Authorization': `Bearer __JSON:token__`
        }
      });

      if (response.ok) {
        const result = await response.json();
        setFormData(result.data);
        setOriginalData(result.data);
      } else if (response.status === 404) {
        alert('__VAR:entity_singular__ not found');
        navigate('/__VAR:entity_plural__');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setErrors({});

    // Send only changed fields
    const changes = {};
    Object.keys(formData).forEach(key => {
      if (formData[key] !== originalData[key]) {
        changes[key] = formData[key];
      }
    });

    // Check if anything changed
    if (Object.keys(changes).length === 0) {
      alert('No changes detected');
      setSaving(false);
      return;
    }

    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer __JSON:token__`
        },
        body: JSON.stringify(changes)
      });

      if (response.ok) {
        const result = await response.json();
        alert('__VAR:entity_singular__ updated successfully!');
        navigate(`/__VAR:entity_plural__/__JSON:id__`);
      } else if (response.status === 422) {
        const data = await response.json();
        setErrors(data.errors);
      } else if (response.status === 404) {
        alert('__VAR:entity_singular__ was deleted');
        navigate('/__VAR:entity_plural__');
      } else {
        throw new Error('Failed to update');
      }
    } catch (error) {
      alert('Error: ' + error.message);
    } finally {
      setSaving(false);
    }
  };

  const handleChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors({ ...errors, [field]: undefined });
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <form onSubmit={handleSubmit}>
      <h1>Edit __VAR:entity_singular__</h1>

      {/* Form fields */}
      <div className="field">
        <label>Name</label>
        <input
          type="text"
          value={formData.name || ''}
          onChange={(e) => handleChange('name', e.target.value)}
          className={errors.name ? 'error' : ''}
        />
        {errors.name && (
          <span className="error-message">
            {errors.name.join(', ')}
          </span>
        )}
      </div>

      {/* More fields... */}

      {/* Show changed fields */}
      {Object.keys(formData).some(k => formData[k] !== originalData[k]) && (
        <div className="changes-indicator">
          Unsaved changes detected
        </div>
      )}

      <div className="actions">
        <button type="submit" disabled={saving}>
          {saving ? 'Saving...' : 'Save Changes'}
        </button>
        <button type="button" onClick={() => navigate(`/__VAR:entity_plural__/__JSON:id__`)}>
          Cancel
        </button>
      </div>
    </form>
  );
}
