```javascript
import { ref, computed, onMounted } from 'vue';
import { useRoute, useRouter } from 'vue-router';

export function useEdit__VAR:entity_singular__() {
  const route = useRoute();
  const router = useRouter();
  const formData = ref({});
  const originalData = ref({});
  const errors = ref({});
  const loading = ref(false);
  const saving = ref(false);

  const hasChanges = computed(() => {
    return Object.keys(formData.value).some(
      key => formData.value[key] !== originalData.value[key]
    );
  });

  const load = async (id) => {
    loading.value = true;

    try {
      const response = await fetch(`/api/__VAR:entity_url__/__JSON:id__`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const result = await response.json();
        formData.value = { ...result.data };
        originalData.value = { ...result.data };
      } else if (response.status === 404) {
        alert('__VAR:entity_singular__ not found');
        router.push('/__VAR:entity_plural__');
      }
    } finally {
      loading.value = false;
    }
  };

  const save = async () => {
    saving.value = true;
    errors.value = {};

    // Get only changed fields
    const changes = {};
    Object.keys(formData.value).forEach(key => {
      if (formData.value[key] !== originalData.value[key]) {
        changes[key] = formData.value[key];
      }
    });

    if (Object.keys(changes).length === 0) {
      alert('No changes to save');
      saving.value = false;
      return false;
    }

    try {
      const response = await fetch(`/api/__VAR:entity_url__/${route.params.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(changes)
      });

      if (response.ok) {
        const result = await response.json();
        router.push(`/__VAR:entity_plural__/${route.params.id}`);
        return true;
      } else if (response.status === 422) {
        const data = await response.json();
        errors.value = data.errors;
        return false;
      } else {
        alert('Failed to save');
        return false;
      }
    } finally {
      saving.value = false;
    }
  };

  onMounted(() => {
    if (route.params.id) {
      load(route.params.id);
    }
  });

  return {
    formData,
    errors,
    loading,
    saving,
    hasChanges,
    load,
    save
  };
}
```


